const err = $input.first().json;
const workflowName = err.workflow?.name || 'Unknown Workflow';
const errorMsg = err.execution?.error?.message || 'Unknown error';
const lastNode = err.execution?.lastNodeExecuted || 'Unknown node';
const executionId = err.execution?.id || 'N/A';

const msg = `⚠️ *OPERATION NUCLEAR FAILURE*

*Workflow:* ${workflowName}
*Error:* ${errorMsg}
*Last Node:* ${lastNode}
*Execution ID:* ${executionId}
*Time:* ${new Date().toISOString()}`;

return { json: { text: msg } };
